import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {Button, PasswordInput, TextInput} from '@mantine/core';




const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password,
      });

      localStorage.setItem('token', response.data.token);
      navigate('/dashboard');
    } catch (error) {
      alert('Giriş başarısız. Bilgileri kontrol edin.');
    }
  };

  return (
  <div className="relative h-screen w-screen overflow-hidden">
    {/* Blurlu arka plan görseli */}
    <div
      className="absolute inset-0 bg-cover bg-center bg-no-repeat blur-sm scale-105"
      style={{ backgroundImage: 'url("ivosis.webp")' }}
    ></div>

    {/* İçerik (net olacak) */}
    <div className="relative z-10 flex flex-col items-center justify-center h-full">
      <div className="w-[450px] bg-white/20 p-10 py-8 rounded-lg">
        <form className='flex flex-col gap-5 [&_input]:placeholder-natural-100 [&_.mantine-Input-input]:!border-white focus-within:[&_.mantine-Input-input]:!border-ivosis-400 [&_.mantine-Input-input]:!border [&_input]:!pl-2 [&_svg]:text-white [&_input]:!text-white'>
          <div className='self-center font-medium font-heading text-black text-xl'>Giriş Yap</div>
          <TextInput className='transition duration-300' variant='unstyled' size='md' radius="md" placeholder='Email' />
          <PasswordInput variant='unstyled' size='md' radius="md" placeholder='Şifre' />
          <Button type='submit' color="ivosis4">Giriş Yap</Button>
        </form>
      </div>
    </div>
  </div>
);

};

export default Login;
