import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password,
      });

      localStorage.setItem('token', response.data.token);
      navigate('/dashboard');
    } catch (error) {
      alert('Giriş başarısız. Bilgileri kontrol edin.');
    }
  };

  return (
    <div className="min-h-screen grid grid-cols-1 md:grid-cols-2 ">
      {/* Sol taraf - Login formu */}
      

      {/* Sağ taraf - Logo */}
      <div className="hidden md:flex items-center justify-center bg-transparent">
        <img
          src="/ivosis.webp"
          alt="logo"
          className="max-w-[70%] h-auto"
        />
      </div>
      <div className="flex items-center justify-center bg-gray-100 p-10">
        <div className="bg-white p-8 rounded-2xl shadow-lg w-full max-w-md">
          <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">
            
            <img
          src="/ivosislogo.png"
          alt="logo"
          className="max-w-[40%] h-auto"
        />
        <h1 className='text-right'>Giriş Yap</h1>
          </h2>
          <form onSubmit={handleLogin} className="space-y-4 bg-white">
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="password"
              placeholder="Şifre"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <button
              type="submit"
              className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition-colors"
            >
              Giriş Yap
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
