import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {Button, PasswordInput, TextInput} from '@mantine/core';




const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password,
      });

      localStorage.setItem('token', response.data.token);
      navigate('/dashboard');
    } catch (error) {
      alert('Giriş başarısız. Bilgileri kontrol edin.');
    }
  };

  return (
    <div style={{background:'url("ivosis.webp")'}} className='h-screen w-screen !bg-cover !bg-center !bg-no-repeat flex flex-col items-center justify-center'>
      <div className='w-[450px] backdrop-blur-sm p-10 py-8 rounded-lg border'>
        <form className='flex flex-col gap-5  [&_input]:placeholder-natural-100 [&_.mantine-Input-input]:!border-white focus-within:[&_.mantine-Input-input]:!border-ivosis-400 [&_.mantine-Input-input]:!border [&_input]:!pl-2 [&_svg]:text-white'>
          <div className='self-center font-medium font-heading text-black text-xl'>Giriş Yap</div>
          <TextInput className='transition duration-300 ' variant='unstyled' size='md' radius="md" placeholder='Email' />
          <PasswordInput variant='unstyled' size='md' radius="md" placeholder='Şifre' />
          <Button type='submit' color="black" >Giriş Yap</Button>
        </form>
      </div>
    </div>
  );
};

export default Login;
