import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Login.css';


const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password
      });

      localStorage.setItem('token', response.data.token);
      navigate('/dashboard');
    } catch (error) {
      alert('Giriş başarısız. Bilgileri kontrol edin.');
    }
  };

  return (
    /*
    <div className="login-container">
      <h2>IVOSIS Project Management</h2>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Şifre"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Giriş Yap</button>
      </form>
    </div>
    */
   <div className='min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12'>
    <div className='relative py-3 sm:max-w-xl sm:mx-auto'>
      <div className='absolute inset-0 bg-gradient-to-r from-cyan-400 to-sky-500 shadow-lg transform -skew-y-6 sm:skew-y-0 sm:-rotate-6 sm:rounded-3xl'></div>
      <div className='relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20'>
        <div className='max-w-md mauto'>
          <div>
            <h1 className='text-2xl font-semibold'>Giriş Yap</h1>
          </div>
          <div className='divide-y divide-gray-200'>
            <div className='py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7'>
              <div className='relative'>
                <input autoComplete='off' id='email' name='email' type='text' className='peer placeholder-transparent h-10 w-full border-b-2 border-gray-300 text-gray-900 focus:outline-none focus:borer-rose-600' placeholder='Email Adresiniz' />
                <label for="email" className='absolute left-0 -top-3.5 text-gray-600 text-sm peer-placeholder-shown:text-base peer-placeholder-shown:text-gray-440 peer-placeholder-shown:top-2 transition-all peer-focus:-top-3.5 peer-focus:text-gray-600 peer-focus:text-sm'>Email Adresiniz</label>
              </div>
              <div className='relative'>
                <input autoComplete='off' id='password' name='password' type='password' className='peer placeholder-transparent h-10 w-full border-b-2 border-gray-300 text-gray-900 focus:outline-none focus:borer-rose-600' placeholder='Şifre' />
                <label for="password" className='absolute left-0 -top-3.5 text-gray-600 text-sm peer-placeholder-shown:text-base peer-placeholder-shown:text-gray-440 peer-placeholder-shown:top-2 transition-all peer-focus:-top-3.5 peer-focus:text-gray-600 peer-focus:text-sm'>Email Adresiniz</label>
              </div>
              <div className='relative'>
                <button className='bg-cyan-500 text-white rounded-md px-2 py-1'>Giriş Yap</button>
              </div>
            </div>
          </div>
        </div>
        
       
      </div>
    </div>
   </div>
  );
};

export default Login;
