import { Avatar, Text } from "@mantine/core";

const Sidebar = () => {
    return(
        <div className="w-64 bg-red-400 flex flex-col gap-7 items-center py-3">
            <div className="text-red-500 flex gap-1 items-center">
                <img src="ivosislogo4.webp" alt="logo" className="w-48"/>
            </div>
            <div className="flex flex-col gap-1 items-center">
                <div className="p-1 bg-white rounded-full shadow-lg">
                    <Avatar variant="filled" src="avatar.png" size="xl" alt="its me" />
                </div>
                <span className="font-medium">Bilgi İşlem</span>
                <Text className="text-natural-500" c="dimmed" size="xs">Admin</Text>
            </div>
        </div>
    )
}

export default Sidebar;
