import React, { useState } from 'react';

const Projects = () => {
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [projects, setProjects] = useState([]);

  // Mock data - normalde API'den gelecek
  const processes = [
    { id: 1, name: 'Analiz' },
    { id: 2, name: 'Tasarım' },
    { id: 3, name: 'Geliştirme' },
    { id: 4, name: 'Test' },
    { id: 5, name: 'Deployment' }
  ];

  const users = [
    { id: 1, name: 'Ahmet Yılmaz', email: 'ahmet@example.com' },
    { id: 2, name: 'Ayşe Demir', email: 'ayse@example.com' },
    { id: 3, name: 'Mehmet Kaya', email: 'mehmet@example.com' },
    { id: 4, name: 'Fatma Şahin', email: 'fatma@example.com' }
  ];

  const statusOptions = [
    { value: 'todo', label: 'Yapılacak', color: 'bg-gray-500' },
    { value: 'inprogress', label: 'Devam Ediyor', color: 'bg-blue-500' },
    { value: 'completed', label: 'Bitti', color: 'bg-green-500' },
    { value: 'cancelled', label: 'İptal', color: 'bg-red-500' }
  ];

  const priorityOptions = [
    { value: 'low', label: 'Düşük', color: 'bg-green-100 text-green-800 border-green-200' },
    { value: 'medium', label: 'Orta', color: 'bg-yellow-100 text-yellow-800 border-yellow-200' },
    { value: 'high', label: 'Yüksek', color: 'bg-orange-100 text-orange-800 border-orange-200' },
    { value: 'critical', label: 'Kritik', color: 'bg-red-100 text-red-800 border-red-200' }
  ];

  // Giriş yapan kullanıcı bilgisi (mock)
  const currentUser = { id: 1, name: 'Ahmet Yılmaz', email: 'ahmet@example.com' };

  const [formData, setFormData] = useState({
    projectName: '',
    processes: [],
    task: '',
    users: [],
    startDate: '',
    endDate: '',
    status: 'todo',
    priority: 'medium',
    lastModifiedBy: currentUser,
    lastModifiedDate: new Date()
  });

  const [dropdownStates, setDropdownStates] = useState({
    processes: false,
    users: false,
    status: false,
    priority: false
  });

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value,
      lastModifiedBy: currentUser,
      lastModifiedDate: new Date()
    }));
  };

  const handleMultiSelect = (field, value) => {
    setFormData(prev => {
      const currentValues = prev[field] || [];
      const newValues = currentValues.includes(value)
        ? currentValues.filter(v => v !== value)
        : [...currentValues, value];
      
      return {
        ...prev,
        [field]: newValues,
        lastModifiedBy: currentUser,
        lastModifiedDate: new Date()
      };
    });
  };

  const toggleDropdown = (field) => {
    setDropdownStates(prev => ({
      ...prev,
      [field]: !prev[field]
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!formData.projectName.trim()) {
      alert('Proje adı gereklidir!');
      return;
    }

    const newProject = {
      id: projects.length + 1,
      ...formData,
      createdDate: new Date()
    };

    setProjects(prev => [...prev, newProject]);
    
    // Form'u temizle
    setFormData({
      projectName: '',
      processes: [],
      task: '',
      users: [],
      startDate: '',
      endDate: '',
      status: 'todo',
      priority: 'medium',
      lastModifiedBy: currentUser,
      lastModifiedDate: new Date()
    });

    alert('Proje başarıyla eklendi!');
    setCurrentPage('dashboard');
  };

  const getPriorityStyle = (priority) => {
    return priorityOptions.find(p => p.value === priority)?.color || 'bg-gray-100 text-gray-800';
  };

  const getStatusStyle = (status) => {
    return statusOptions.find(s => s.value === status)?.color || 'bg-gray-500';
  };

  // Dashboard görünümü
  if (currentPage === 'dashboard') {
    return (
      <div className="min-h-screen bg-gray-50 p-6">
        <div className="max-w-7xl mx-auto">
          <div className="flex justify-between items-center mb-8">
            <div>
              <h1 className="text-3xl font-bold text-gray-900">Proje Yönetimi</h1>
              <p className="text-gray-600 mt-2">Projelerinizi yönetin ve takip edin</p>
            </div>
            <button
              onClick={() => setCurrentPage('addProject')}
              className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg flex items-center gap-2 transition-colors"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
              </svg>
              Proje Ekle
            </button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {projects.length === 0 ? (
              <div className="col-span-full text-center py-12">
                <div className="text-gray-400 text-6xl mb-4">📋</div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">Henüz proje yok</h3>
                <p className="text-gray-600 mb-6">İlk projenizi ekleyerek başlayın</p>
                <button
                  onClick={() => setCurrentPage('addProject')}
                  className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg flex items-center gap-2 mx-auto transition-colors"
                >
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
                  </svg>
                  Proje Ekle
                </button>
              </div>
            ) : (
              <table className="w-full text-sm text-left border border-gray-200">
  <thead className="bg-gray-100 text-gray-700 font-semibold">
    <tr>
      <th className="p-2">Proje</th>
      <th className="p-2">Tarih</th>
      <th className="p-2">Öncelik</th>
      <th className="p-2">Durum</th>
      <th className="p-2">Kullanıcı</th>
      <th className="p-2">Son Güncelleyen</th>
    </tr>
  </thead>
  <tbody>
    {projects.map(project => (
      <tr key={project.id} className="border-t hover:bg-gray-50">
        <td className="p-2">{project.projectName}</td>
        <td className="p-2">{project.startDate} - {project.endDate}</td>
        <td className="p-2">{priorityOptions.find(p => p.value === project.priority)?.label}</td>
        <td className="p-2">{statusOptions.find(s => s.value === project.status)?.label}</td>
        <td className="p-2">{project.users.length} kullanıcı</td>
        <td className="p-2 text-xs">{project.lastModifiedBy.name}<br />{project.lastModifiedDate.toLocaleString('tr-TR')}</td>
      </tr>
    ))}
  </tbody>
</table>

            )}
          </div>
        </div>
      </div>
    );
  }

  // Proje ekleme sayfası
  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-4xl mx-auto">
        <div className="flex items-center mb-8">
          <button
            onClick={() => setCurrentPage('dashboard')}
            className="mr-4 px-4 py-2 text-gray-600 hover:text-gray-800 flex items-center gap-2"
          >
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
            Geri Dön
          </button>
          <div>
            <h1 className="text-3xl font-bold text-gray-900">Yeni Proje Ekle</h1>
            <p className="text-gray-600 mt-2">Proje bilgilerini doldurun</p>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-lg p-8">
          <div className="space-y-6">
            {/* Proje Adı */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Proje Adı *</label>
                <input
                  type="text"
                  required
                  value={formData.projectName}
                  onChange={(e) => handleInputChange('projectName', e.target.value)}
                  maxLength={250}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Proje adını girin"
                />
                <div className="text-xs text-gray-500 mt-1">{formData.projectName.length}/250 karakter</div>
              </div>

              {/* Süreçler */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Süreçler</label>
                <div className="relative">
                  <div
                    className="w-full px-3 py-2 border border-gray-300 rounded-md bg-white cursor-pointer min-h-[42px] flex items-center justify-between"
                    onClick={() => toggleDropdown('processes')}
                  >
                    <div className="flex-1">
                      {formData.processes.length === 0 ? (
                        <span className="text-gray-500">Süreç seçin</span>
                      ) : (
                        <div className="flex flex-wrap gap-1">
                          {formData.processes.map((processId) => (
                            <span key={processId} className="bg-blue-100 text-blue-800 px-2 py-1 rounded text-xs">
                              {processes.find(p => p.id === processId)?.name}
                            </span>
                          ))}
                        </div>
                      )}
                    </div>
                    <svg className="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                    </svg>
                  </div>
                  
                  {dropdownStates.processes && (
                    <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg max-h-60 overflow-auto">
                      {processes.map((process) => (
                        <div
                          key={process.id}
                          className={`px-3 py-2 cursor-pointer hover:bg-gray-100 flex items-center ${
                            formData.processes.includes(process.id) ? 'bg-blue-50 text-blue-700' : ''
                          }`}
                          onClick={() => handleMultiSelect('processes', process.id)}
                        >
                          <input
                            type="checkbox"
                            checked={formData.processes.includes(process.id)}
                            onChange={() => {}}
                            className="mr-2"
                          />
                          {process.name}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* Görev */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Görev</label>
              <textarea
                value={formData.task}
                onChange={(e) => handleInputChange('task', e.target.value)}
                maxLength={250}
                rows={3}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                placeholder="Görev açıklamasını girin"
              />
              <div className="text-xs text-gray-500 mt-1">{formData.task.length}/250 karakter</div>
            </div>

            {/* Kullanıcılar */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Kullanıcılar</label>
              <div className="relative">
                <div
                  className="w-full px-3 py-2 border border-gray-300 rounded-md bg-white cursor-pointer min-h-[42px] flex items-center justify-between"
                  onClick={() => toggleDropdown('users')}
                >
                  <div className="flex-1">
                    {formData.users.length === 0 ? (
                      <span className="text-gray-500">Kullanıcı seçin</span>
                    ) : (
                      <div className="flex flex-wrap gap-1">
                        {formData.users.map((userId) => (
                          <span key={userId} className="bg-green-100 text-green-800 px-2 py-1 rounded text-xs">
                            {users.find(u => u.id === userId)?.name}
                          </span>
                        ))}
                      </div>
                    )}
                  </div>
                  <svg className="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                  </svg>
                </div>
                
                {dropdownStates.users && (
                  <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg max-h-60 overflow-auto">
                    {users.map((user) => (
                      <div
                        key={user.id}
                        className={`px-3 py-2 cursor-pointer hover:bg-gray-100 flex items-center ${
                          formData.users.includes(user.id) ? 'bg-blue-50 text-blue-700' : ''
                        }`}
                        onClick={() => handleMultiSelect('users', user.id)}
                      >
                        <input
                          type="checkbox"
                          checked={formData.users.includes(user.id)}
                          onChange={() => {}}
                          className="mr-2"
                        />
                        <div>
                          <div className="font-medium">{user.name}</div>
                          <div className="text-sm text-gray-500">{user.email}</div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>

            {/* Tarihler */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Başlama Tarihi</label>
                <input
                  type="date"
                  value={formData.startDate}
                  onChange={(e) => handleInputChange('startDate', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Bitirme Tarihi</label>
                <input
                  type="date"
                  value={formData.endDate}
                  onChange={(e) => handleInputChange('endDate', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>

            {/* Durum ve Önem */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Durum</label>
                <div className="relative">
                  <div
                    className="w-full px-3 py-2 border border-gray-300 rounded-md bg-white cursor-pointer min-h-[42px] flex items-center justify-between"
                    onClick={() => toggleDropdown('status')}
                  >
                    <div className="flex items-center">
                      <div className={`w-3 h-3 rounded-full ${getStatusStyle(formData.status)} mr-3`}></div>
                      {statusOptions.find(s => s.value === formData.status)?.label}
                    </div>
                    <svg className="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                    </svg>
                  </div>
                  
                  {dropdownStates.status && (
                    <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg max-h-60 overflow-auto">
                      {statusOptions.map((status) => (
                        <div
                          key={status.value}
                          className={`px-3 py-2 cursor-pointer hover:bg-gray-100 ${
                            formData.status === status.value ? 'bg-blue-50 text-blue-700' : ''
                          }`}
                          onClick={() => {
                            handleInputChange('status', status.value);
                            toggleDropdown('status');
                          }}
                        >
                          <div className="flex items-center">
                            <div className={`w-3 h-3 rounded-full ${status.color} mr-3`}></div>
                            {status.label}
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Önem Sırası</label>
                <div className="relative">
                  <div
                    className="w-full px-3 py-2 border border-gray-300 rounded-md bg-white cursor-pointer min-h-[42px] flex items-center justify-between"
                    onClick={() => toggleDropdown('priority')}
                  >
                    <div className="flex items-center">
                      <svg className={`w-4 h-4 mr-2 ${
                        formData.priority === 'critical' ? 'text-red-600' : 
                        formData.priority === 'high' ? 'text-orange-600' : 
                        formData.priority === 'medium' ? 'text-yellow-600' : 'text-green-600'
                      }`} fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M3 3a1 1 0 000 2v8a2 2 0 002 2h2.586l-1.293 1.293a1 1 0 101.414 1.414L10 15.414l2.293 2.293a1 1 0 001.414-1.414L12.414 15H15a2 2 0 002-2V5a1 1 0 100-2H3zm11.707 4.707a1 1 0 00-1.414-1.414L10 9.586 8.707 8.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                      </svg>
                      {priorityOptions.find(p => p.value === formData.priority)?.label}
                    </div>
                    <svg className="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                    </svg>
                  </div>
                  
                  {dropdownStates.priority && (
                    <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg max-h-60 overflow-auto">
                      {priorityOptions.map((priority) => (
                        <div
                          key={priority.value}
                          className={`px-3 py-2 cursor-pointer hover:bg-gray-100 ${
                            formData.priority === priority.value ? 'bg-blue-50 text-blue-700' : ''
                          }`}
                          onClick={() => {
                            handleInputChange('priority', priority.value);
                            toggleDropdown('priority');
                          }}
                        >
                          <div className="flex items-center">
                            <svg className={`w-4 h-4 mr-2 ${
                              priority.value === 'critical' ? 'text-red-600' : 
                              priority.value === 'high' ? 'text-orange-600' : 
                              priority.value === 'medium' ? 'text-yellow-600' : 'text-green-600'
                            }`} fill="currentColor" viewBox="0 0 20 20">
                              <path fillRule="evenodd" d="M3 3a1 1 0 000 2v8a2 2 0 002 2h2.586l-1.293 1.293a1 1 0 101.414 1.414L10 15.414l2.293 2.293a1 1 0 001.414-1.414L12.414 15H15a2 2 0 002-2V5a1 1 0 100-2H3zm11.707 4.707a1 1 0 00-1.414-1.414L10 9.586 8.707 8.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                            </svg>
                            {priority.label}
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* Sistem Bilgileri */}
            <div className="bg-gray-50 p-4 rounded-lg">
              <h3 className="text-sm font-medium text-gray-700 mb-2">Sistem Bilgileri</h3>
              <div className="text-sm text-gray-600 space-y-1">
                <div>Son Değiştiren: {formData.lastModifiedBy.name}</div>
                <div>Son Değişiklik: {formData.lastModifiedDate.toLocaleString('tr-TR')}</div>
              </div>
            </div>

            {/* Butonlar */}
            <div className="flex justify-end space-x-4 pt-6">
              <button
                type="button"
                onClick={() => setCurrentPage('dashboard')}
                className="px-6 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
              >
                İptal
              </button>
              <button
                onClick={handleSubmit}
                className="px-6 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg flex items-center gap-2 transition-colors"
              >
                <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                </svg>
                Projeyi Kaydet
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Projects;
